import React from 'react';
import Carousel from 'react-bootstrap/Carousel';
import Container from 'react-bootstrap/Container';

const AuthorSlider = () => {
  const authors = [
    { id: 1, name: 'Author 1', photo: 'https://cdn.pixabay.com/photo/2020/07/01/12/58/icon-5359553_1280.png' },
    { id: 2, name: 'Author 2', photo: 'https://cdn.pixabay.com/photo/2020/07/01/12/58/icon-5359553_1280.png' },
    { id: 3, name: 'Author 3', photo: 'https://cdn.pixabay.com/photo/2020/07/01/12/58/icon-5359553_1280.png' },
    // Add more authors as needed
  ];

  return (
    <Container>
      <h2 style={{ textAlign: "center", color: "navy", margin: '3vw' }}>Author Slider</h2>
      <Carousel>
        {authors.map(author => (
          <Carousel.Item key={author.id}>
            <div className="circle-image">
              <img
                className="d-block w-100 rounded-circle"
                src={author.photo}
                alt={`Author ${author.id}`}
              />
              <h3 style={{ textAlign: "center", color: "navy" }}>{author.name}</h3>
            </div>
          </Carousel.Item>
        ))}
      </Carousel>
    </Container>
  );
};

export default AuthorSlider;
