import React, { useState } from 'react';
import Card from 'react-bootstrap/Card';
import Container from 'react-bootstrap/Container';
import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';

const BookCategories = () => {
  const categories = [
    {
      name: 'Adventure stories',
      description: 'Adventure novels whisk readers off to faraway lands. Unlike fantasy novels, they tend to stay in the real world. They’re designed to spark imaginations.',
      image: 'https://example.com/fiction-image.jpg',
      author: 'Adventure Author',
      famousBook: 'The Epic Journey',
    },
    {
      name: 'Historical fiction',
      description: 'Books give readers a glimpse into the past, with many stories set in times of great conflict and change.',
      image: 'https://example.com/fiction-image.jpg',
      author: 'Historical Author',
      famousBook: 'The Time Chronicles',
    },
    { 
      name: 'Horror',
      description: 'A spine-chilling tale of supernatural terror that will haunt your dreams long after you turn the last page.',
      image: 'https://example.com/fiction-image.jpg',
      author: 'Horror Author',
      famousBook: 'The Haunting Shadows',
    },
    // Add more categories as needed
  ];

  const [showModal, setShowModal] = useState(false);
  const [selectedCategory, setSelectedCategory] = useState(null);

  const handleMoreClick = (index) => {
    setSelectedCategory(categories[index]);
    setShowModal(true);
  };

  const handleClose = () => {
    setShowModal(false);
  };

  return (
    <>
      <h2 style={{ textAlign: "center" }}>Book Categories</h2>

      <Container>
        {categories.map((category, idx) => (
          <Card key={idx} className='mb-3' style={{ backgroundImage: `url(${category.image})`, backgroundSize: 'cover', backgroundPosition: 'center', color: 'white' }}>
            <Card.Body>
              <Card.Title><h3>{category.name}</h3></Card.Title>
              <button type="button" className="btn btn-secondary mt-4 py-4" style={{ background: "rgb(158 163 221)" }} onClick={() => handleMoreClick(idx)}>More...</button>
            </Card.Body>
          </Card>
        ))}
      </Container>

      <Modal show={showModal} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>{selectedCategory?.name}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <p>Description: {selectedCategory?.description}</p>
          <p>Author: {selectedCategory?.author}</p>
          <p>Famous Book: {selectedCategory?.famousBook}</p>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
        </Modal.Footer>
      </Modal>
    </>
  );
};

export default BookCategories;
