import React from 'react';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';
import Container from 'react-bootstrap/Container';

const AuthorCarousel = () => {
  const authors = [
    { id: 1, name: 'Author 1', photo: 'https://d2g9wbak88g7ch.cloudfront.net/authorimages/arundhatistory_647_100316054702.jpg' },
    { id: 2, name: 'Author 2', photo: 'https://d2g9wbak88g7ch.cloudfront.net/authorimages/dr-shashi-tharoor-2.jpg' },
    { id: 3, name: 'Author 3', photo: 'https://d2g9wbak88g7ch.cloudfront.net/authorimages/stephan-coonts.jpg' },
    { id: 4, name: 'Author 4', photo: 'https://d2g9wbak88g7ch.cloudfront.net/authorimages/dillian.jpg' },
    { id: 5, name: 'Author 5', photo: 'https://d2g9wbak88g7ch.cloudfront.net/authorimages/stephen-edwin-king.jpg' },
    { id: 6, name: 'Author 6', photo: 'https://d2g9wbak88g7ch.cloudfront.net/authorimages/william-wordsworth-8.jpg' },
    { id: 7, name: 'Author 7', photo: 'https://d2g9wbak88g7ch.cloudfront.net/authorimages/ma10_milestones_segal.jpg' },
    // Add more authors as needed
  ];

  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 3,
    slidesToScroll: 1,
    responsive: [
      {
        breakpoint: 768,
        settings: {
          slidesToShow: 1,
          slidesToScroll: 1,
        },
      },
    ],
  };

  return (
    <Container>
      
      <Slider {...settings}>
        {authors.map((author) => (
          <div key={author.id} className="author-slide">
            <img
              src={author.photo}
              alt={`Author ${author.id}`}
              className="rounded-circle author-image"
            />
            <h4 style={{ textAlign: "center", color: "navy" }}>{author.name}</h4>
          </div>
        ))}
      </Slider>
    </Container>
  );
};

export default AuthorCarousel;
