import { Button } from 'bootstrap';
import React from 'react';
import Card from 'react-bootstrap/Card';
import Container from 'react-bootstrap/Container';

const BookCategories = () => {
  const categories = [
    {
      name: 'Adventure stories',
      description: 'Adventure novels whisk readers off to faraway lands. Unlike fantasy novels, they tend to stay in the real world. They’re designed to spark imaginations.',
      image: 'https://example.com/fiction-image.jpg',
    },
    {
        name: 'Fiction',
        description: 'Explore the imaginative world of fiction books.',
        image: 'https://example.com/fiction-image.jpg',
      },
      
     { name: 'Fiction',
      description: 'Explore the imaginative world of fiction books.',
      image: 'https://example.com/fiction-image.jpg',
    },
    {
      name: 'Non-Fiction',
      description: 'Discover informative non-fiction books.',
      image: 'https://example.com/non-fiction-image.jpg',
    },
    {
      name: 'Mystery',
      description: 'Get lost in the thrill of mysterious stories.',
      image: 'https://example.com/mystery-image.jpg',
    },
    {
        name: 'Mystery',
        description: 'Get lost in the thrill of mysterious stories.',
        image: 'https://example.com/mystery-image.jpg',
      },
      {
        name: 'Mystery',
        description: 'Get lost in the thrill of mysterious stories.',
        image: 'https://example.com/mystery-image.jpg',
      },
    {
      name: 'Science Fiction',
      description: 'Immerse yourself in futuristic and scientific adventures.',
      image: 'https://example.com/science-fiction-image.jpg',
    },
    // Add more categories as needed
  ];

  return (
    <>
    <h2 style={{textAlign: "center"}}>Book Categories</h2>

    <Container>
      {categories.map((category, idx) => (
        <Card key={idx} className='mb-3 py-5'>
          {/* <Card.Img variant='top' src={category.image} /> */}
          <Card.Body>
            <Card.Title><h3>{category.name}</h3></Card.Title>
            <Card.Text>{category.description}</Card.Text>
            <button type="button" className="btn btn-secondary my-4" style={{background:" rgb(158 163 221)"}}>Stories</button>

          </Card.Body>
        </Card>
      ))}
    </Container>
    </>
  );
};

export default BookCategories;
