import React from 'react';
import Card from 'react-bootstrap/Card';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

const BookCategories = () => {
  const categories = [
    {
      name: 'Fiction',
      description: 'Explore the imaginative world of fiction books.',
      image: 'https://example.com/fiction-image.jpg',
    },
    {
      name: 'Non-Fiction',
      description: 'Discover informative non-fiction books.',
      image: 'https://example.com/non-fiction-image.jpg',
    },
    {
      name: 'Mystery',
      description: 'Get lost in the thrill of mysterious stories.',
      image: 'https://example.com/mystery-image.jpg',
    },
    {
      name: 'Science Fiction',
      description: 'Immerse yourself in futuristic and scientific adventures.',
      image: 'https://example.com/science-fiction-image.jpg',
    },
    // Add more categories as needed
  ];

  return (
    <Container>
      <h2 className='text-center m-5'>Book Categories</h2>
      <Row className="d-flex justify-content-between">
        {categories.map((category, idx) => (
          <Col key={idx} className="mb-4" style={{ flex: '0 0 23%' }}>
            <Card>
              <Card.Img variant="top" src={category.image} style={{ height: '150px', objectFit: 'cover' }} />
              <Card.Body>
                <Card.Title style={{ fontSize: '1rem', fontWeight: 'bold' }}>{category.name}</Card.Title>
                <Card.Text style={{ fontSize: '0.8rem' }}>
                  {category.description}
                </Card.Text>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>
    </Container>
  );
}

export default BookCategories;
