// import React from 'react';
import Card from 'react-bootstrap/Card';
import Container from 'react-bootstrap/Container';
import React, { useState } from 'react';

const BookCategories = () => {
  const categories = [
    {
      name: 'Adventure stories',
      description: 'Adventure novels whisk readers off to faraway lands. Unlike fantasy novels, they tend to stay in the real world. Theyâ€™re designed to spark imaginations.',
      image: 'https://example.com/fiction-image.jpg',
    },
    {
      name: 'Historical fiction',
      description: 'Books give readers a glimpse into the past, with many stories set in times of great conflict and change.',
      image: 'https://example.com/fiction-image.jpg',
    },
    { 
      name: 'Horror',
      description: 'A spine-chilling tale of supernatural terror that will haunt your dreams long after you turn the last page.',
      image: 'https://example.com/fiction-image.jpg',
    },
    {
      name: 'Humour and satire',
      description: 'A witty and irreverent exploration of life absurdities, blending humor and satire to provoke laughter and contemplation simultaneously.',
     
    },
    {
      name: 'Mystery',
      description: 'An enigmatic journey filled with suspense, twists, and hidden clues, keeping readers on the edge of their seats until the final revelation.',
      
    },
    {
      name: 'Science fiction',
      description: 'A mind-bending exploration of futuristic worlds, advanced technologies, and the boundless possibilities of the cosmos, challenging the limits of imagination.',
      
    },
    {
      name: 'Autobiography and memoir',
      description: 'A candid and introspective narrative, recounting the author life journey with heartfelt reflections on personal triumphs, challenges, and transformative experiences.',
      
    },
    {
      name: 'Biography',
      description: 'A compelling and detailed account of a person life, exploring their achievements, challenges, and impact on the world',
      
    },
    // Add more categories as needed
  ];
  const [expandedCategory, setExpandedCategory] = useState(null);

  const handleMoreClick = (index) => {
    setExpandedCategory(index === expandedCategory ? null : index);
  };


  return (
    <>
      <h2 style={{ textAlign: "center", color: "navy" , margin:'5vw'}}>Best Author</h2>

      <Container >
        {categories.map((category, idx) => (
          <Card key={idx} className='mb-3' style={{ backgroundImage: `url(https://png.pngtree.com/thumb_back/fh260/back_our/20190622/ourmid/pngtree-creative-synthesis-simple-book-education-and-training-background-image_219497.jpg)`, backgroundSize: 'cover', backgroundPosition: 'center', color: 'white' }}>
            <Card.Body style={{ color: "black" }}>
              <Card.Title style={{ color: "navy",margin:"1vw" }}><h1>{category.name}</h1></Card.Title>
              {expandedCategory === idx ? (
            <>
                  <Card.Text className='my-4'style={{ color: "black",margin:"1vw" }}><h3>{category.description}</h3></Card.Text>
                  <p style={{ color: "black",margin:"1vw" }} ><h3>Famous Author: {category.author}</h3></p>
                  <p style={{ color: "black",margin:"1vw" }}><h3>Famous Book: {category.famousBook}</h3></p>
                  <button type="button" className="btn btn-secondary mt-4 py-4" style={{ background: "navy", color:"white" ,margin:"1vw", padding:'0.5vw'}}  onClick={() => handleMoreClick(idx)}>Less</button>
               </> 
              ) : (
                <button type="button" className="btn btn-secondary mt-4 py-4" style={{ background: "navy", color:"white" ,margin:"1vw", padding:'0.5vw'}}  onClick={() => handleMoreClick(idx)}>More...</button>
              )}
              {/* <Card.Body style={{ color: "black" }}>
              <Card.Title style={{ color: "navy",margin:"1vw" }}><h3>{category.name}</h3></Card.Title>
              <Card.Text className='my-4'><h5>{category.description}</h5></Card.Text>
              <button type="button" className="btn btn-secondary" style={{ background: "navy", color:"white" ,margin:"1vw", padding:'0.5vw'}}>More...</button> */}
            </Card.Body>
          </Card>
        ))}
      </Container>
    </>
  );
};

export default BookCategories;
