import React from 'react';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';
import './SlidingBar.css'; // Create this CSS file for styling

const authors = [
  { name: 'Author 1', photo: 'https://cdn.pixabay.com/photo/2020/07/01/12/58/icon-5359553_1280.png' },
  { name: 'Author 2', photo: 'url-to-author-2-photo.jpg' },
  { name: 'Author 3', photo: 'url-to-author-3-photo.jpg' },
  // Add more authors as needed
];

const SlidingBar = () => {
  const settings = {
    infinite: true,
    speed: 500,
    slidesToShow: 3,
    slidesToScroll: 1,
    centerMode: true,
    variableWidth: true,
    focusOnSelect: true,
  };

  return (
    <div className="sliding-bar-container">
      <Slider {...settings}>
        {authors.map((author, index) => (
          <div key={index} className="author-circle">
            <img src={author.photo} alt={author.name} />
            <p>{author.name}</p>
          </div>
        ))}
      </Slider>
    </div>
  );
};

export default SlidingBar;
